import React, { useState } from "react";
import { useParams,useHistory } from "react-router-dom";

function UpdateAnimal({updateAnimal}){
  //declare param functions
  const params=useParams()
  const {id}=useParams()
  //declare navigation function
  const navigate=useHistory()

  //declare our object variables

    const [name, setName] = useState("")
    const [breed, setBreed] = useState("")
    const [age, setAge] = useState("")
    const [gender, setGender] = useState("")
    const [size, setSize] = useState("")
    const [weight, setWeight] = useState("")
    const [personality, setPersonality] = useState("")
    const [healthStatus, setHealthStatus] = useState("")
    const [availabilityStatus, setaAvailabilityStatus] = useState("")
    const [image, setImage] = useState("")


  function handleSubmit(e) {
    e.preventDefault();

    //create object to hold data
    const updateanimal = {
      name:name,
      breed:breed,
      age:age,
      gender:gender,
      size:size,
      weight:weight,
      personality:personality,
      healthStatus:healthStatus,
      availabilityStatus:availabilityStatus,
      image:image
    }

    fetch(`http://localhost:3000/animals/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(updateanimal),
    })
      .then((response) => response.json())
      .then((data) => updateanimal(data))

      //clearing input fields
      setInputClear()
      //navigate to home page
      navigate.push("/");
  }

  function setInputClear() {
    setName("")
    setBreed("")
    setAge("")
    setGender("")
    setSize("")
    setWeight("")
    setPersonality("")
    setHealthStatus("")
    setaAvailabilityStatus("")
    setImage("")
  }

  return (
      <form className="form" onSubmit={handleSubmit}>
        <h1 style={{textAlign:"center"}}>UPDATE ANIMAL</h1>
        <label for="name">Name</label>
          <input type="text" className="form-name" id="name" value={name} placeholder="Enter Animal Name" onChange={e => setName(e.target.value)}/>

          <label for="name">Breed</label>
          <input type="text" id="breed" value={breed} placeholder="Enter Breed" onChange={e => setBreed(e.target.value)}/>

          <label for="name">Breed</label>
          <input type="text" id="breed" value={breed} placeholder="Enter Breed" onChange={e => setBreed(e.target.value)}/>


        <label>
          Age:
          <input
            type="text"
            name="age"
            value={formData.age}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Gender:
          <input
            type="text"
            name="gender"
            value={formData.gender}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Size:
          <input
            type="text"
            name="size"
            value={formData.size}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Weight:
          <input
            type="text"
            name="weight"
            value={formData.weight}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Personality:
          <input
            type="text"
            name="personality"
            value={formData.personality}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Health status:
          <input
            type="text"
            name="healthStatus"
            value={formData.healthStatus}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Availability status:
          <input
            type="text"
            name="availabilityStatus"
            value={formData.availabilityStatus}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Image URL:
          <input
            type="text"
            name="image"
            value={formData.image}
            onChange={handleInputChange}
          />
        </label>

       <button className="submit" type="submit">ENTER</button>
      </form>
  );

  }
export default UpdateAnimal;
